import axios from 'axios'
import crypto from 'crypto'

import { DIFFIE_HELMAN, DIFFIE_HELMAN_END } from './actionTypes'

export function Dh() {
    return dispatch => {
        const DHData = {
            Dh: true
        }

    let url = 'http://localhost:8888/auth';

    let body = JSON.stringify(DHData);
    console.log(body)
    axios({
        method: 'post',
        url: url,
        data: body,
      }).then((response) => {
          console.log(response.data);

          const prime = JSON.parse(JSON.stringify(response.data.serverPrime), (key, value) => { 

            return value && value.type === 'Buffer'
                  ? Buffer.from(value.data)
                  : value;
          });
          const generator = JSON.parse(JSON.stringify(response.data.serverGenerator), (key, value) => { 

            return value && value.type === 'Buffer'
                  ? Buffer.from(value.data)
                  : value;
          });
          const serverPubKey = JSON.parse(JSON.stringify(response.data.serverPublicKey), (key, value) => { 

            return value && value.type === 'Buffer'
                  ? Buffer.from(value.data)
                  : value;
          });
          console.log(prime)
          console.log(generator)
          console.log(serverPubKey)
          let client = crypto.createDiffieHellman(prime, generator)     
          let pubAndPrivteKey = client.generateKeys();



          let secret = client.computeSecret(serverPubKey)
          console.log("secret", secret)
        //   dispatch(DHSuccess(parseData.data.IP, 
        //                      parseData.data.prime, 
        //                      parseData.data.generator, 
        //                      parseData.data.serverPublicKey
        //                     ))
      })
      .catch(function (error) {
          console.log(error);
      });
    }
}


export function DHSuccess(IP, prime, generator, serverPublicKey) {
    console.log("DIFFIE_HELMAN")
    return {
        type: DIFFIE_HELMAN,
        IP,
        prime,
        generator,
        serverPublicKey
    }
}
