import React, {Component} from 'react'
import Button from '../../component/Button/Button'
import Input from '../../component/Input/Input'
import axios from 'axios';
import { connect } from 'react-redux';
import {auth} from  '../../store/actions/auth'

import classes from './auth.module.css'

class Auth extends Component {

  state = {
    formControls: {
        email: {
          value: '',
          type: 'email',
          label: 'Email',
          errorMessage: 'Input correct email',
          valid: false,
          touched: false,
          validation: {
            required: true,
            email: true
          }
        },
        password: {
          value: '',
          type: "password",
          label: "Password",
          errorMessage: 'Inputcorrect password',
          valid: false,
          touched: false,
          validation: {
            required: true,
            minLength: 6
          }

        }
    }
  };
  config = {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
  }
  handleInputChange = this.handleInputChange.bind(this);
  loginHandler = this.loginHandler.bind(this);
  registerHandler = this.registerHandler.bind(this);


  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  loginHandler() {    
    let body = JSON.stringify(this.state);

    axios({
        method: 'post',
        url: 'http://localhost:8888/auth',
        data: body,

    }).then((response) => {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }
  registerHandler() {

  }

  submitHandler = event => {
    event.preventDefault()
  }

  validateControl(value, validation) {
    if(!validation){
      return true
    }

    let isValid = true

    if(validation.required) {
      isValid = value.trim() != '' && isValid
    }

    if(validation.email) {

    }

    if(validation.minLength) {
      isValue = value.length >= validation.minLength && isValid 
    }

  }

  onChangeHandler = (event, controlName) => {

    const formsControls = {...this.state.formControls}
    const control = {...formControls[controlName]}

    control.value = event.target.value
    control.touched = true
    control.valid = this.validateControl(control.value, control.validation)

    formControl[controlName] = control

    this.setState({
      formControls
    })
  }


  renderInputs() {
    return Object.keys(this.state.formControls).map(() => {
      const control = this.state.formControls[controlName]
      return (
        <Input 
          key={controlName}
          type={control.type}
          value={control.value}
          valid={control.valid}
          touched={control.touched}
          label= {control.label}
          shouldValidate={!!control.validation}
          errorMessage={control.errorMessage}
          onChange={event => this.onChangeHandler(event, controlName)}
        />
      )
    })
  }

  render() {

    return (
      <div className={classes.Auth}>
        <div>
          <h1>Authorization</h1>
            <form onSubmit={this.submitHandler} className={classes.AuthForm}>

              {this.renderInputs() }    

              <Button label={"Login"} handleClick={this.loginHandler}/>
              <Button label={"Registration"} handleClick={this.registerHandler}/>

              {this.state.response}

            </form>
        </div>
      </div>
    );
  }
}
function mapDispatchToProps(dispatch) {
  return { 
    auth: (email, password, isLogin) => dispatch(auth(email, password, isLogin))
  }
}

export default connect(null, mapDispatchToProps)(Auth);
