import React, {Component} from 'react'
import { connect } from 'react-redux';

import getDataUser from  '../../store/actions/getDataUser'
import MainFunctionBar from '../../component/MainFunctionBar/MainFunctionBar'
import classes from './HomePage.module.css'

class HomePage extends Component {
    constructor() {
        super();
        this.state = {
          response: false,
          endpoint: "http://localhost:8000"
        };
    }
    componentDidMount(){
        this.props.getDataUser();
        console.log(this.props.loading)
    }


    render() {
        console.log(this.props.loading)
        if(this.props.loading) {
            return (<h1>загрузка</h1>)
        }
        else{
        return(
            <div>
            {console.log(this.props)}
            {console.log(this.props.loading)}

                <div>
                    <div className={classes.leftSideBar}>
                        <MainFunctionBar/>
                    </div >
                    <div>
                    <span color="#ffffff">Вы вошли как: {this.props.email} </span><br/>
                    <span color="#ffffff">Ваш id: {this.props.id}</span>

                    </div>
                </div>
            </div>
        )
    }
    }
}

function mapStateToProps(state) {
    return {
        id: state.getDataUser.id,
        email: state.getDataUser.email,
        loading: state.getDataUser.loading
    }
}

function mapDispatchToProps(dispatch) {
    return { 
      getDataUser: () => dispatch(getDataUser())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
